package gov.saip.applicationservice;import net.javacrumbs.shedlock.spring.annotation.EnableSchedulerLock;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.security.servlet.SecurityAutoConfiguration;import org.springframework.cloud.openfeign.EnableFeignClients;import org.springframework.data.jpa.repository.config.EnableJpaAuditing;import org.springframework.scheduling.annotation.EnableAsync;import org.springframework.scheduling.annotation.EnableScheduling;import javax.annotation.PostConstruct;import java.util.TimeZone;import static gov.saip.applicationservice.util.Constants.SAUDI_ARABIA_TIMEZONE;@SpringBootApplication(exclude = {SecurityAutoConfiguration.class })@EnableFeignClients@EnableJpaAuditing@EnableScheduling@EnableAsync@EnableSchedulerLock(defaultLockAtMostFor = "PT30M")public class ApplicationServiceApplication{    public static void main(String[] args) {        SpringApplication.run(ApplicationServiceApplication.class, args);    }    @PostConstruct    void started() {        TimeZone.setDefault(TimeZone.getTimeZone(SAUDI_ARABIA_TIMEZONE));    }}