INSERT INTO application.classifications (id,created_by_user,created_date,modified_by_user,modified_date,is_deleted,code,enabled,name_ar,name_en,nice_version,description_ar,description_en) VALUES
	 (1,'',NULL,'',NULL,0,'1',true,'فئة 1','',0,'الكيماويات المستخدمة في الصناعة والبحث العلمي والتصوير الفوتوغرافي وكذلك في الزراعة والبستنة وزراعة الغابات، راتنجات اصطناعية غير معالجة، بلاستيك غير معالج، أسمدة، مركبات إخماد النـيران، مستحضرات سقي ولحام المعادن، مواد كيميائية لحفظ المواد الغذائية، مواد دبـاغة، مواد اللصق المستخدمة في الصناعة.',NULL),
	 (2,'',NULL,'',NULL,0,'2',true,'فئة 2','',0,'الدهانات والورنيش وطلاءات اللك، مواد الوقاية من الصدأ ومواد حفظ الخشب من التلف، مواد التلوين، مواد تثبيت الألوان، راتنج طبيعي خام، معادن في شكل رقائق أو مسحوق لإستخدام الدهانين وفنيي الديكور وعمال الطباعة والفنانين.',NULL),
	 (3,'',NULL,'',NULL,0,'3',true,'فئة 3','',0,'مستحضرات تبييض الأقمشة ومواد أخرى تستعمل في غسل وكي الملابس، مستحضرات تنظيف وصقل وجلي وكشط، صابون، عطور وزيوت عطرية، مستحضرات تجميل، غسول (لوشن) للشعر، منظفات أسنان.',NULL),
	 (4,'',NULL,'',NULL,0,'4',true,'فئة 4','',0,'زيوت وشحوم صناعية، مزلقات، مركبات امتصاص وترطيب وتثبيت الغبار، وقود (بما في ذلك وقود المحركات)، مواد إضاءة، شموع وفتائل للإضاءة.',NULL),
	 (5,'',NULL,'',NULL,0,'5',true,'فئة 5','',0,'مستحضرات صيدلانية وبيطرية، مستحضرات صحية لغايات طبية، مواد وأغذية حمية معدة للإستعمال الطبي أو البيطري وأغذية للرضع والأطفال، مكملات للحمية الغذائية للإنسان والحيوان؛ لصقات ومواد ضماد، مواد حشو الأسنان وشمع طب الأسنان، مطهرات، مستحضرات لإبادة الحشرات والحيوانات الضارة، مبيدات فطريات ومبيدات أعشاب.',NULL),
	 (6,'',NULL,'',NULL,0,'6',true,'فئة6','',0,'معادن غير نفيسة وكل خليط منها، مواد بناء معدنية، مباني متنقلة معدنية، مواد معدنية لخطوط السكك الحديدية، حبال وأسلاك غير كهربائية من معادن غير نفيسة، مصنوعات حدادة، خردوات معدنية صغيرة، مواسير وأنابيب معدنية، خزائن حفظ الوثائق والأشياء الثمينة، منتجات مصنوعة من معادن غير نفيسة غير واردة في فئات أخرى، خامات معادن.',NULL),
	 (7,'',NULL,'',NULL,0,'7',true,'فئة 7','',0,'آلات وعدد آلية، محركات ومكائن (عدا ما كان منها للمركبات البرية) ، قارنات آلية وعناصر نقل الحركة (عدا ما كان منها للمركبات البرية) ، معدات زراعية (عدا ما يدار باليد) ، أجهزة تفقيس البيض؛ آلات بيع أوتوماتية. ',NULL),
	 (8,'',NULL,'',NULL,0,'8',true,'فئة 8','',0,'عدد وأدوات يدوية (تُدار باليد)، أدوات قطع (الشُّوك والسكاكين والملاعق)، أسلحة بيضاء، أدوات حلاقة.',NULL),
	 (9,'',NULL,'',NULL,0,'9',true,'فئة 9','',0,'الأجهزة والأدوات العلمية والملاحية والمساحية وأجهزة وأدوات التصوير الفوتوغرافي والسينمائي والأجهزة والأدوات البصرية وأدوات الوزن والقياس والإشارة والمراقبة (الإشراف) والإنقاذ والتعليم، أجهزة وأدوات لوصل أو فتح أو تحويل أو تكثيف أو تنظيم أو التحكم في الطاقة الكهربائية، أجهزة تسجيل أو إرسال أو نسخ الصوت أو الصور، حاملات بيانات مغناطيسية، أقراص تسجيل، أقراص مدمجة، أقراص فيديوية رقمية وغيرها من وسائط التسجيل الرقمية؛ آليات للأجهزة التي تعمل بقطع النقد، آلات تسجيل النقد، آلات حاسبة، معدات معالجة البيانات، أجهزة الكمبيوتر؛ برامج كمبيوتر؛ أجهزة إطفاء الحرائق',NULL),
	 (10,'',NULL,'',NULL,0,'10',true,'فئة 10','',0,'أجهزة وأدوات جراحية وطبية وطب أسنان وبيطرية، أطراف وعيون وأسنان إصطناعية، أدوات تجبير، مواد خياطة أو درز الجروح.',NULL);
INSERT INTO application.classifications (id,created_by_user,created_date,modified_by_user,modified_date,is_deleted,code,enabled,name_ar,name_en,nice_version,description_ar,description_en) VALUES
	 (11,'TMS',NULL,'',NULL,0,'11',true,'فئة11','',0,'أجهزة للإنارة والتدفئة وتوليد البخار والطهو والتبريد والتجفيف والتهوية وإمداد المياه والأغراض الصحية. ',NULL),
	 (12,'TMS',NULL,'',NULL,0,'12',true,'فئة12','',0,'المركبات وأجهزة النقل البري أو الجوي أو المائي.',NULL),
	 (13,'TMS',NULL,'',NULL,0,'13',true,'فئة13','',0,'الأسلحة النارية والذخائر والقذائف والمتفجرات والألعاب النارية.',NULL),
	 (14,'TMS',NULL,'',NULL,0,'14',true,'فئة14','',0,'المعادن النفيسة وكل خليط منها والمنتجات المصنوعة من معادن نفيسة أو مطلية بها، غير الواردة في فئات أخرى، المجوهرات والأحجار الكريمة، أدوات قياس الوقت وأدوات قياس الوقت الدقيقة. ',NULL),
	 (15,'TMS',NULL,'',NULL,0,'15',true,'فئة15','',0,'آلات موسيقية.',NULL),
	 (16,'TMS',NULL,'',NULL,0,'16',true,'فئة16','',0,'الورق والورق المقوى والمنتجات المصنوعة من هذه المواد وغير واردة في فئات أخرى ؛ المطبوعات ؛ مواد تجليد الكتب؛ الصور الفوتوغرافية ؛ القرطاسية؛ مواد اللصق المستعملة  في القرطاسية أو لغايات منزلية ؛ ومواد الفنانين ؛ فراشي الدهان أو التلوين ؛ الآلات الكاتبة واللوازم المكتبية (عدا الأثاث)؛ مواد التوجيه والتدريس (عدا الأجهزة) ؛ مواد التغليف البلاستيكية (غير الواردة في فئات أخرى)؛ حروف الطباعة ؛ الكليشيهات (الراسمات).',NULL),
	 (17,'TMS',NULL,'',NULL,0,'17',true,'فئة17','',0,'المطاط والغوتابرشا والصمغ والأسبستوس والميكا والمنتجات المصنوعة من هذه المواد وغير واردة في فئات أخرى ، مواد بلاستيكية متشكلة بالبثق للإستعمال في التصنيع ، مواد التغليف والحشو والعزل ، أنابيب مرنة غير معدنية.',NULL),
	 (18,'TMS',NULL,'',NULL,0,'18',true,'فئة18','',0,'الجلود المدبوغة والجلود المدبوغة المقلدة والمنتجات المصنوعة من هذه المواد غير الواردة في فئات أخرى، جلود الحيوانات الخام أو المدبوغة، الصناديق والحقائب السفرية ، المظلات والشماسي والعصي، السياط وأطقم الحيوانات والسروج.',NULL),
	 (19,'TMS',NULL,'',NULL,0,'19',true,'فئة19','',0,'مواد بناء (غير معدنية) ، أنابيب قاسية غير معدنية للمباني، أسفلت وزفت وقار، مباني غير معدنية قابلة للنقل، نصب (مجسمات) غير معدنية.',NULL),
	 (20,'TMS',NULL,'',NULL,0,'20',true,'فئة20','',0,'الأثاث والمرايا واطارات الصور، المنتجات (غير الواردة في فئات أخرى) المصنوعة من الخشب أو الفلين أو القصب أو الخيزران أو الصفصاف او السندان أو العظام أو العاج أو عظم فك الحوت أو الصدف أو الكهرمان أو المحار أو المرشوم والمواد البديلة لكل هذه المواد أو من المواد البلاستيكية.',NULL);
INSERT INTO application.classifications (id,created_by_user,created_date,modified_by_user,modified_date,is_deleted,code,enabled,name_ar,name_en,nice_version,description_ar,description_en) VALUES
	 (21,'TMS',NULL,'',NULL,0,'21',true,'فئة21','',0,'أدوات وأواني وأوعية للاستعمال المنزلي وللمطبخ، أمشاط وأسفنج، فراشي (عدا فراشي التلوين أو الدهان)، مواد صنع الفراشي، أدوات تنظيف، سلك جلي، زجاج غير مشغول أو زجاج شبه مشغول (عدا الزجاج المستعمل في المباني)، أواني زجاجية وأواني خزف صيني وأواني خزفية غير واردة في فئات أخرى.',NULL),
	 (22,'TMS',NULL,'',NULL,0,'22',true,'فئة22','',0,'الحبال والخيوط والشبكات والخيم والمظلات والمشمع والأشرعة والغرارات والأكياس (غير الواردة في فئات أخرى)، مواد التبطين والحشو (عدا ما كان من المطاط أو البلاستيك)، مواد نسيج من الألياف الخام.',NULL),
	 (23,'TMS',NULL,'',NULL,0,'23',true,'فئة23','',0,'الغزل والخيوط المستخدمة في النسيج.',NULL),
	 (24,'TMS',NULL,'',NULL,0,'24',true,'فئة24','',0,'المنسوجات ومنتجات النسيج غير الواردة في فئات أخرى ، أغطية الفراش والموائد. ',NULL),
	 (25,'TMS',NULL,'',NULL,0,'25',true,'فئة25','',0,'الملابس ولباس القدم وأغطية الرأس.',NULL),
	 (26,'TMS',NULL,'',NULL,0,'26',true,'فئة26','',0,'المخرمات والمطرزات والأشرطة والجدائل ، الأزرار والكلابات والعراوي والدبابيس والإبر والزهور الإصطناعية. ',NULL),
	 (27,'TMS',NULL,'',NULL,0,'27',true,'فئة27','',0,'السجاد والبسط والحصر ومفارش الحصير ومشمع فرش الأرضيات ومواد أخرى لتغطية الأرضيات القائمة ومطرزات تعلق على الجدران (من مواد غير نسيجية). ',NULL),
	 (28,'TMS',NULL,'',NULL,0,'28',true,'فئة28','',0,'اللعب وأدوات اللعب، أدوات الرياضة الجمنازية والأدوات الرياضية غير الواردة في فئات أخرى وأشجار الزينة',NULL),
	 (29,'TMS',NULL,'',NULL,0,'29',true,'فئة29','',0,'اللحوم والأسماك ولحوم الدواجن والصيد، خلاصات اللحم، فواكه وخضروات محفوظة ومجففة ومطهوة، هلام (جيلي) ومربيات وفواكه مطبوخة بالسكر، البيض والحليب ومنتجات الحليب، الزيوت والدهون الصالحة للأكل.',NULL),
	 (30,'TMS',NULL,'',NULL,0,'30',true,'فئة30','',0,' القهوة والشاي والكاكاو والقهوة الاصطناعية؛ الأرز، المعكرونة والنودلز، التابيوكا والساغو؛ والدقيق والمستحضرات المصنوعة من الحبوب؛ الخبز والفطائر والحلويات؛ الشوكولاتة، بوظة والحلويات المثلجة، شربات ومثلجات أخرى صالحة للأكل؛ السكر وعسل النحل والعسل الأسود؛ والخميرة ومسحوق الخبيز؛ والملح؛ التوابل والأعشاب المحفوظة. الخل والصلصات والتوابل الأخرى؛ الثلج. (المياه المُثلجة).',NULL);
INSERT INTO application.classifications (id,created_by_user,created_date,modified_by_user,modified_date,is_deleted,code,enabled,name_ar,name_en,nice_version,description_ar,description_en) VALUES
	 (31,'TMS',NULL,'',NULL,0,'31',true,'فئة31','',0,'الغِلال والمنتجات الزراعية، ومنتجات البساتين والغابات غير الواردة في فئات أخرى، الحيوانات الحية، الفواكه والخضروات الطازجة، البذور والنباتات والزهور الطبيعية، المواد الغذائية الخاصة بالحيوانات، الشعير المنبت (الملت). ',NULL),
	 (32,'TMS',NULL,'',NULL,0,'32',true,'فئة32','',0,'البيرة (شراب الشعير) والمياه المعدنية والغازية وغيرها من المشروبات غير الكحولية، مشروبات مستخلصة من الفواكه وعصائر الفواكه، شراب ومستحضرات أخرى لعمل المشروبات.',NULL),
	 (34,'TMS',NULL,'',NULL,0,'34',true,'فئة34','',0,'التبغ وأدوات المدخنين وأعواد الثقاب وبدائل التبغ؛ السجائر والسيجار، والسجائر الإلكترونية وأجهزة التبخير الفموية للمدخنين؛ وأدوات المدخنين وأعواد الكبريت',NULL),
	 (35,'TMS',NULL,'',NULL,0,'35',true,'فئة35','',0,'خدمات الدعاية والإعلان وإدارة وتوجيه الأعمال وتفعيل النشاط المكتبي.',NULL),
	 (36,'TMS',NULL,'',NULL,0,'36',true,'فئة36','',0,'خدمات التأمين، الشؤون التمويلية، الشؤون المالية والشؤون العقارية.',NULL),
	 (37,'TMS',NULL,'',NULL,0,'37',true,'فئة37','',0,'إنشاء المباني والإصلاح وخدمات التركيب أو التجميع.',NULL),
	 (38,'TMS',NULL,'',NULL,0,'38',true,'فئة38','',0,'الاتصالات.',NULL),
	 (39,'TMS',NULL,'',NULL,0,'39',true,'فئة39','',0,'النقـل وتغليف وتخزين السـلع وتنظيم الرحلات والسفر.',NULL),
	 (40,'TMS',NULL,'',NULL,0,'40',true,'فئة40','',0,'معالجة المواد.',NULL),
	 (41,'TMS',NULL,'',NULL,0,'41',true,'فئة41','',0,'التعليم والتهذيب، التدريب، الترفيه، الأنشطة الرياضية والثقافية.',NULL);
INSERT INTO application.classifications (id,created_by_user,created_date,modified_by_user,modified_date,is_deleted,code,enabled,name_ar,name_en,nice_version,description_ar,description_en) VALUES
	 (42,'TMS',NULL,'',NULL,0,'42',true,'فئة42','',0,'الخدمات العلمية والتقنية وخدمات البحث والتصميم المتعلقة بها، خدمات التحاليل والأبحاث الصنـاعية، خدمـات تصميم وتطوير عتاد وبرامج الكمبيوتـر.',NULL),
	 (43,'TMS',NULL,'',NULL,0,'43',true,'فئة43','',0,'خدمات توفير الأطعمة والمشروبات، الإيواء المؤقت.',NULL),
	 (44,'TMS',NULL,'',NULL,0,'44',true,'فئة44','',0,'الخدمات الطبية، الخدمات البيطرية، خدمات العناية الصحية والمحافظة على الجمال للكائنات البشرية أو الحيوانات، خدمات الزراعة والبستنة والغابات.',NULL),
	 (45,'TMS',NULL,'',NULL,0,'45',true,'فئة45','',0,'خدمات قانونية وخدمات أمنية لحماية الممتلكات والأفراد وخدمات شخصية واجتماعية يقدمها آخرون تلبية لحاجات الأفراد.',NULL),
